-- Comprobamos que no existe la base de datos, si existe, la eliminamos

DROP DATABASE IF EXISTS proyecto;

-- Creación de la base de datos

CREATE DATABASE proyecto;
USE proyecto;

-- Creación de la tabla familias

CREATE TABLE familias (
    cod VARCHAR(6) PRIMARY KEY,
    nombre VARCHAR(200)
);

-- Creación de la tabla productos

CREATE TABLE productos (
    id INT(11) PRIMARY KEY,
    nombre VARCHAR(200) NOT NULL,
    nombre_corto VARCHAR(50) NOT NULL,
    descripcion TEXT,
    pvp DECIMAL(10,2) NOT NULL,
    familia_cod VARCHAR(6),
    FOREIGN KEY (familia_cod) REFERENCES familias(cod)
);

-- Creación de la tabla tiendas

CREATE TABLE tiendas (
    id INT(11) PRIMARY KEY,
    nombre VARCHAR(100) NOT NULL,
    tln VARCHAR(13)
);

-- Creación de la tabla stocks

CREATE TABLE stocks (
    producto INT(11),
    tienda INT(11),
    unidades INT(10) UNSIGNED,
    PRIMARY KEY (producto, tienda),
    FOREIGN KEY (producto) REFERENCES productos(id),
    FOREIGN KEY (tienda) REFERENCES tiendas(id)
);

-- Eliminar el usuario si ya existe

DROP USER IF EXISTS 'gestor'@'localhost';

-- Crear el usuario "gestor" con contraseña "secreto"

CREATE USER 'gestor'@'localhost' IDENTIFIED BY 'secreto';

-- Conceder permisos al usuario "gestor" para la base de datos "proyecto"

GRANT ALL PRIVILEGES ON proyecto.* TO 'gestor'@'localhost';
FLUSH PRIVILEGES;

